import { useParams } from "react-router-dom";
import useFetch from "./useFetch";

const BlogDetails = () => {
  const { id } = useParams();
  const {
    data: blog,
    isPending,
    error,
  } = useFetch("http://localhost:3000/blog/" + id);
  return (
    <div className="blog-details">
      {isPending && <div>Loading....</div>}
      {error && { error }}
      {data && (
        <article>
          {blog.title}
          {blog.author}
          {blog.body}
        </article>
      )}
    </div>
  );
};

export default BlogDetails;
